mapper.select:
SELECT am.`subject` AS 'contentType', am.time ct, am.body content, am.type, ac.withJid AS 'to', ac.ownerJid AS 'from', vto.vcard as 'toCard', vfrom.vcard as 'fromCard'
FROM archiveMessages am LEFT JOIN archiveConversations ac ON ac.conversationId = am.conversationId LEFT JOIN ofVCard vto on CONCAT(vto.username,'@126xmpp') = ac.withJid LEFT JOIN ofVCard vfrom on CONCAT(vfrom.username,'@126xmpp') = ac.ownerJid WHERE am.direction = 'to' AND ownerJid = #{to,jdbcType=VARCHAR} AND withJid = #{from,jdbcType=VARCHAR}
UNION
SELECT am.`subject` AS 'contentType', am.time ct, am.body content, am.type, ac.withJid AS 'to', ac.ownerJid AS 'from', vto.vcard as 'toCard', vfrom.vcard as 'fromCard' FROM archiveMessages am LEFT JOIN archiveConversations ac ON ac.conversationId = am.conversationId LEFT JOIN ofVCard vto on CONCAT(vto.username,'@126xmpp') = ac.withJid LEFT JOIN ofVCard vfrom on CONCAT(vfrom.username,'@126xmpp') = ac.ownerJid WHERE am.direction = 'to' AND ownerJid = #{from,jdbcType=VARCHAR} AND withJid = #{to,jdbcType=VARCHAR}
ORDER BY ct DESC LIMIT #{fromCount,jdbcType=VARCHAR}, #{count,jdbcType=VARCHAR}
